export default function renderContentfulImports({
  compatibleWithRealResponses,
}: RenderingOptions): string {
  return `
    // THIS FILE IS AUTOMATICALLY GENERATED. DO NOT MODIFY IT.

    import { Asset, Entry, Sys } from 'contentful';
    import { Document } from '@contentful/rich-text-types';

    export interface PatchedSys extends Sys {
      ${compatibleWithRealResponses ? "[otherKey: string]: any" : ""}
    }

    ${
      compatibleWithRealResponses
        ? `
        export interface PatchedAsset {
          sys: Omit<PatchedSys, 'contentType'>
          fields: Partial<Asset['fields']>
          [otherKey: string]: any
        }`
        : " export interface PatchedAsset extends Asset {};"
    }

    export interface PatchedEntrySys<T> {
      id: string;
      type: string;
      createdAt: string;
      updatedAt: string;
      locale: string;
      contentType: {
        sys: {
          id: T;
          linkType: 'ContentType';
          type: 'Link';
          [otherKey: string]: any;
        }
      };
      ${compatibleWithRealResponses ? "[otherKey: string]: any" : ""}
    }

    export interface PatchedEntry<T, I> {
      fields: Entry<T>['fields']
      sys: PatchedEntrySys<I>
      ${compatibleWithRealResponses ? "[otherKey: string]: any" : ""}
    }
  `
}
