import renderContentfulImports from "../../../src/renderers/contentful/renderContentfulImports"
import _format from "../../support/format"

describe("renderContentfulImports()", () => {
  it("renders the top of the codegen file", () => {
    const output = renderContentfulImports({ compatibleWithRealResponses: false })

    expect(output).toMatchInlineSnapshot(`
      "
          // THIS FILE IS AUTOMATICALLY GENERATED. DO NOT MODIFY IT.

          import { Asset, Entry, Sys } from 'contentful';
          import { Document } from '@contentful/rich-text-types';

          export interface PatchedSys extends Sys {
            
          }

           export interface PatchedAsset extends Asset {};

          export interface PatchedEntrySys<T> {
            id: string;
            type: string;
            createdAt: string;
            updatedAt: string;
            locale: string;
            contentType: {
              sys: {
                id: T;
                linkType: 'ContentType';
                type: 'Link';
                [otherKey: string]: any;
              }
            };
            
          }

          export interface PatchedEntry<T, I> {
            fields: Entry<T>['fields']
            sys: PatchedEntrySys<I>
            
          }
        "
    `)
  })
})
